import React, { ChangeEvent, RefObject, useEffect, useRef, useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCalendar, faCirclePlus, faXmark } from '@fortawesome/free-solid-svg-icons';
import { Task } from '../model/task';

import '../styles/Modal.css';
import "react-datepicker/dist/react-datepicker.css";
import DatePicker from 'react-datepicker';
import InputComponent from './InputComponent';

const addZeros = (n: number) => n<10? '0'+n:''+n;
const formatDate = (d: Date) => `${addZeros(d.getDate())}/${addZeros(d.getMonth() + 1)}/${d.getFullYear()}`;

function Modal({open, task, onSave, onClose}:{open: boolean, task: Task, onSave: (task: Task) => void, onClose: () => void}) {
  const dialogRef = useRef<HTMLDialogElement>(null);
  const [todo, setTodo] = useState<Task>(task);
  const [isCalendarOpen, setIsCalendarOpen] = useState<boolean>(false);
  const [submitted, setSubmitted] = useState<boolean>(false);

  const errorMessage = (fieldName: string) => { `O campo ${fieldName} é obrigatório.` }

  const toggleCalendar = () => {
    setIsCalendarOpen(!isCalendarOpen);
  }

  const handleCalendarChange = (date: Date) => {
    setTodo({...todo, date});
    toggleCalendar();
  }  

  useEffect(() => {
    if(open) {
      dialogRef.current?.showModal();
    } else {
      dialogRef.current?.close();
    }
  }, [open])

  // useEffect(() => {
  //   const currentRef = dialogRef.current;

  //   const handleClickOutside = (event: Event) => {
  //     if(event.target == currentRef) {
  //       onClose();
  //     }
  //   }

  //   currentRef?.addEventListener('click', handleClickOutside);
  //   return () => {
  //     currentRef?.removeEventListener('click', handleClickOutside);
  //   }

  // }, [dialogRef])

  const handleSave = () => {
    onSave(todo);
  };

  return (
    <dialog className='modal' ref={dialogRef}>
      <div className='modal-header'>
      <h3 className='title'>{task?.id ? 'Editar' : 'Adicionar'} tarefa</h3>
      <FontAwesomeIcon className='x-icon' onClick={() => onClose()} icon={faXmark}></FontAwesomeIcon>
      </div>
    <div className='form'>
      <InputComponent
        placeholder='Adicione o título'
        label='Título'
        name='title'
        value={todo.title}
        onChange={value => setTodo({...todo, title: value})}
        errorMessage=''
      />

      <InputComponent
        placeholder='Adicione a descrição'
        label='Descrição'
        name='description'
        value={todo.description}
        onChange={value => setTodo({...todo, description: value})}
        errorMessage=''
      />



      <div className='datepicker'>


      <InputComponent
        placeholder='Adicione a data'
        label='Data'
        name='dueDate'
        readonly
        value={todo?.date ? formatDate(todo.date) : ''}
        onChange={value => console.log(value)}
        errorMessage=''
      />

        <FontAwesomeIcon className='calendar-icon' onClick={toggleCalendar} icon={faCalendar}></FontAwesomeIcon>
        </div>
        {isCalendarOpen && 
        <DatePicker
          inline
          calendarClassName='calendar'
          selected={todo.date}
          onChange={handleCalendarChange}
        />}
        <button className='button' onClick={handleSave}><FontAwesomeIcon icon={faCirclePlus}></FontAwesomeIcon>&nbsp;Adicionar</button>
      </div>
    </dialog>
  );
}

export default Modal;
