import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCirclePlus } from '@fortawesome/free-solid-svg-icons';

import InputComponent from './components/InputComponent';
import './App.css';


function App() {
  const tasks: any[] = [];

  const addTask = () => {
    tasks.push({title: '', description: '', edit: true, state: ''});
  }

  return (
    <div className="App">
      <header className="App-header">
        <div className='Content'>
          <p>LEMBRETES</p>
          <div className='List'>
            <p className='None'> Nenhum lembrete dispon√≠vel. </p>
            {tasks.map(({title, description, edit, state}, i) => (
              <InputComponent key={i}></InputComponent>
            ))}
            <button className='Add-button' onClick={addTask}>
              <FontAwesomeIcon icon={faCirclePlus}></FontAwesomeIcon>&nbsp;
              Novo Lembrete
            </button>
          </div>
        </div>
      </header>
    </div>
  );
}

export default App;
