import React, { ReactNode } from 'react';

import '../styles/InputComponent.css';
import DatePicker from 'react-datepicker';

type InputProps = {placeholder: string, type?: string, label:string, name: string, value?: string | number, readonly?: boolean, onChange: (value: string) => void, errorMessage?:ReactNode}

const DatepickerComponent = ({ name, label, placeholder, errorMessage, value, onChange }: InputProps) => {
  return (
    <div className="wrapper">
      <label className='label' htmlFor={name}>
        {label}
      </label>
      <DatePicker
        className='inpue'
        startDate={new Date()}
          selected={value ? new Date(value) :null}
          onChange={() => onChange('a')}
          dateFormat={'dd/MM/yyyy'}
        />
      {
        errorMessage &&
        <span className='error-message'>{errorMessage}</span>
      }
    </div>
  );
}

function InputComponent({type, ...props}: InputProps) {
  if(type === 'datepicker') 
  return <DatepickerComponent {...props} />

  const {placeholder, label, name, value, onChange, errorMessage} = props

  return (
    <div className="wrapper">
      <label className='label' htmlFor={name}>
        {label}
      </label>
      <input
        type={type}
        className='input'
        placeholder={placeholder}
        name={name}
        value={value}
        onChange={(e) => onChange(e.target.value)}
      />
      {
        errorMessage &&
        <span className='error-message'>{errorMessage}</span>
      }
    </div>
  );
}

export default InputComponent;
