import React, { ReactNode } from 'react';

import '../styles/InputComponent.css';

const DatepickerComponent = ({name, value, }) => {
  return (
    <div className="wrapper">
      <label className='label' htmlFor={name}>
        {label}
      </label>
      <input
        type='text'
        className='input'
        placeholder={placeholder}
        readOnly={readonly}
        name={name}
        value={value}
        onChange={(e) => onChange(e.target.value)}
      />
      {
        errorMessage &&
        <span className='error-message'>{errorMessage}</span>
      }
    </div>
  );
}

function InputComponent(props: {placeholder: string, type: string, label:string, name: string, value: string, readonly?: boolean, onChange: (value: string) => void, errorMessage?:ReactNode}) {
  if(type === 'datepicker') 
  return <DatepickerComponent />

  const {placeholder, type, label, name, value, onChange, readonly, errorMessage} = props

  return (
    <div className="wrapper">
      <label className='label' htmlFor={name}>
        {label}
      </label>
      <input
        type='text'
        className='input'
        placeholder={placeholder}
        readOnly={readonly}
        name={name}
        value={value}
        onChange={(e) => onChange(e.target.value)}
      />
      {
        errorMessage &&
        <span className='error-message'>{errorMessage}</span>
      }
    </div>
  );
}

export default InputComponent;
