import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCalendar } from '@fortawesome/free-solid-svg-icons';
import DatePicker from 'react-datepicker';

import '../styles/InputComponent.css';
import { Todo } from '../model/todo';

function InputComponent({_todo}:{_todo: Todo}) {
  // function InputComponent({todo, saveTodo}: {todo: any, saveTodo: () => void}) {
  const [todo, setTodo] = useState<Todo>(_todo);
  const [description, setDescription] = useState('');
  const [checked, setChecked] = useState(false);
  const [date, setDate] = useState(new Date());

  const handleChange = () => {
    setChecked(!checked);
  };

  return (
    <div className='Form'>
      <input 
        type='checkbox' 
        checked={checked}
        onChange={handleChange}
      />
      <div className='Inputs'>
      <input 
        type='text'
        className='Title'
        placeholder='Adicionar título' 
        value={todo.title}
        onChange={event => setTodo({...todo, title: event.target.value})}
      />
      <input 
        type='text'
        className='Description'
        placeholder='Adicionar descrição'
        value={description}
        onChange={event => setDescription(event.target.value)}
        />
      </div>
        <FontAwesomeIcon 
          className='Calendar' 
          icon={faCalendar}
          onClick={() => { <DatePicker selected={date} onChange={(date: Date) => setDate(date)} /> }}
          ></FontAwesomeIcon>
    </div>
  );
}

export default InputComponent;
