import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCalendar, faCircleCheck } from '@fortawesome/free-solid-svg-icons';
import { Task } from '../model/task';

import '../styles/InputComponent.css';
import "react-datepicker/dist/react-datepicker.css";
import DatePicker from 'react-datepicker';

function InputComponent({_todo}:{_todo: Task}) {
  const [todo, setTodo] = useState<Task>(_todo);
  const [isEdit, setIsEdit] = useState<boolean>(false);
  const [isCalendarOpen, setIsCalendarOpen] = useState<boolean>(false);

  const toggleCalendar = () => {
    setIsCalendarOpen(!isCalendarOpen);
  }

  const handleChange = () => {
    setTodo({...todo, checked: !todo.checked});
  };

  // const handleSave = () => {
  //   if (isEdit) {
  //     return editTask(Number(id), editPayload());
  //   }
  //   return createTask(createPayload());
  // };

  return (
    <>
    <div className='form'>
      <input 
        type='checkbox' 
        checked={todo.checked}
        onChange={handleChange}
      />
      <div className='Inputs'>
        <div className="lala">

        <input 
          type='text'
          className='Title'
          placeholder='Adicionar título' 
          value={todo.title}
          onChange={event => setTodo({...todo, title: event.target.value})}
          />
        <button onClick={toggleCalendar} > 
        <FontAwesomeIcon icon={faCalendar}></FontAwesomeIcon>
      </button>
      <button className='Confirm-button'>
      <FontAwesomeIcon icon={faCircleCheck}></FontAwesomeIcon>&nbsp;
      Adicionar
    </button>
      </div>
      {
        isCalendarOpen &&
        <DatePicker 
        showPopperArrow={false}
        inline
        selected={todo.date} 
        dateFormat={"dd/MM/yyyy"} 
        onChange={toggleCalendar}
        />
      }
        <input 
          type='text'
          className='Description'
          placeholder='Adicionar descrição'
          value={todo.description}
          onChange={event => setTodo({...todo, description: event.target.value})}
        />
      </div>
      <div>


      </div>

    </div>
    </>
  );
}

export default InputComponent;
