import React from 'react';

import '../styles/InputComponent.css';

function InputComponent({placeholder, label, name, value, onChange, readonly, errorMessage}: {placeholder: string, label:string, name: string, value: string, readonly?: boolean, onChange: (value: string) => void, errorMessage?:string}) {
  return (
    <div className="wrapper">
      <label className='label' htmlFor={name}>
        {label}
      </label>
      <input
        type='text'
        className='input'
        placeholder={placeholder}
        readOnly={readonly}
        name={name}
        value={value}
        onChange={(e) => onChange(e.target.value)}
      />
      {
        errorMessage &&
        <span className='error-message'>{errorMessage}</span>
      }
    </div>
  );
}

export default InputComponent;
