import InputComponent from './InputComponent';

function ListComponent() {
  const tasks: any[] = [{title: 'skjflkd', description: 'fhdsjf', edit: true, state: 'CHECKED'}];

  const addTask = () => {
    tasks.push();
    console.log('chegou');
  }

  return (
    <div className='List'>
      {tasks.length > 0 ? 
      tasks.map(({title, description, edit, state}, i) => (
        <InputComponent _todo={{title: title}} key={i}></InputComponent>
        )) :
        <p className='None'> Nenhum lembrete dispon√≠vel. </p>
      }
      <button className='Add-button' onClick={addTask}>
        <FontAwesomeIcon icon={faCirclePlus}></FontAwesomeIcon>&nbsp;
          Novo Lembrete
      </button>
    </div>
  );
}

export default ListComponent;
