import React, { ChangeEvent, RefObject, useEffect, useRef, useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faAdd, faCalendar, faCircleCheck, faCirclePlus, faPencil, faTrash } from '@fortawesome/free-solid-svg-icons';
import { Task } from '../model/task';

import '../styles/Modal.css';
import "react-datepicker/dist/react-datepicker.css";
import DatePicker from 'react-datepicker';
import InputComponent from './InputComponent';

const addZeros = (n: number) => n<10? '0'+n:''+n;
const formatDate = (d: Date) => `${addZeros(d.getDate())}/${addZeros(d.getMonth() + 1)}/${d.getFullYear()}`;

function Modal({open, task, onSave}:{open: boolean, task: Task, onSave: (task: Task) => void}) {
  const dialogRef = useRef<HTMLDialogElement>(null);
  const [todo, setTodo] = useState<Task>(task);
  const [isCalendarOpen, setIsCalendarOpen] = useState<boolean>(false);


  const toggleCalendar = () => {
    setIsCalendarOpen(!isCalendarOpen);
  }

  const handleCalendarChange = (date: Date) => {
    setTodo({...todo, date});
    toggleCalendar();
  }  

  useEffect(() => {
    if(open) {
      dialogRef.current?.showModal();
    }
  }, [open])

  const handleSave = () => {
    onSave(todo);
  };

  return (
    <dialog className='modal' ref={dialogRef}>
      <h3 className='title'>{task?.id ? 'Editar' : 'Adicionar'} tarefa</h3>
    <div className='form'>
      <InputComponent
        placeholder='Adicione o título'
        label='Título'
        name='title'
        value={todo.title}
        onChange={value => setTodo({...todo, title: value})}
        errorMessage=''
      />

      <InputComponent
        placeholder='Adicione a descrição'
        label='Descrição'
        name='description'
        value={todo.description}
        onChange={value => setTodo({...todo, description: value})}
        errorMessage=''
      />



      <div className='datepicker'>


      <InputComponent
        placeholder='Adicione a data'
        label='Data'
        name='dueDate'
        readonly
        value={todo?.date ? formatDate(todo.date) : ''}
        onChange={value => console.log(value)}
        errorMessage=''
      />

        <FontAwesomeIcon className='calendar-icon' onClick={toggleCalendar} icon={faCalendar}></FontAwesomeIcon>
        </div>
        {isCalendarOpen && <DatePicker
        inline
        calendarClassName='calendar'
        selected={todo.date}
        onChange={handleCalendarChange}
        />}
        <button className='button'><FontAwesomeIcon onClick={toggleCalendar} icon={faCirclePlus}></FontAwesomeIcon>&nbsp;Adicionar</button>
      </div>
    </dialog>
  );
}

export default Modal;
