import React, { useCallback, useEffect, useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCirclePlus } from '@fortawesome/free-solid-svg-icons';

import InputComponent from './InputComponent';
import '../styles/ListComponent.css';
import { CreateTask, Task } from '../model/task';
import { createTask, deleteTask, editTask, getAll } from '../services/TaskService';

function ListComponent() {

  const [tasks, setTasks] = useState<Task[]>([]);

  const fetchTasks = useCallback(async () => {
    try {
      const response: Task[] = await getAll();
      setTasks(response);
    } catch (err) {
      console.log(err);
    }
  }, []);

  useEffect(() => {
    fetchTasks();
  }, [fetchTasks]);

  const addTask = () => {
    setTasks([...tasks, {title: '', description: '', checked: false, id: '' }]);
  }

  const handleDeleteTask = ({index, id}: { index: number, id?:string}) => {
    if(id) {
      deleteTask(id)
    }
    const array = [...tasks]
    array.splice(index, 1)
    console.log(array)
    setTasks(array)
  }

  const saveTask = async (task: Task) => {
    if(task?.id) {
      await editTask(task)
    } else {
      await createTask(task);
    }
  }

  return (
    <div className='content'>
      <p>LEMBRETES</p>
      <div className='list'>
        {tasks?.length > 0 ? 
        tasks.map((task, i) => (
          <InputComponent onDelete={() => handleDeleteTask({id: task.id, index: i})} task={task} onSave={saveTask} key={i}></InputComponent>
          )) :
          <p className='None'> Nenhum lembrete dispon√≠vel. </p>
        }
        <button className='Add-button' onClick={addTask}>
          <FontAwesomeIcon icon={faCirclePlus}></FontAwesomeIcon>&nbsp;
            Novo Lembrete
        </button>
      </div>
    </div>
  );
}

export default ListComponent;
