import React, { ChangeEvent, useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCalendar, faCircleCheck, faPencil } from '@fortawesome/free-solid-svg-icons';
import { Task } from '../model/task';

import '../styles/InputComponent.css';
import "react-datepicker/dist/react-datepicker.css";
import DatePicker from 'react-datepicker';

function InputComponent({task, onSave}:{task: Task, onSave: (task: Task) => void}) {
  const [todo, setTodo] = useState<Task>(task);
  const [isEdit, setIsEdit] = useState<boolean>(false);
  const [isCalendarOpen, setIsCalendarOpen] = useState<boolean>(false);

  const toggleCalendar = () => {
    setIsCalendarOpen(!isCalendarOpen);
  }
  const toggleEdit = () => {
    setIsEdit(!isEdit);
  }

  const handleCalendarChange = (date: Date) => {
setTodo({...todo, date});
toggleCalendar();
  }  
  const handleChange = () => {
    setTodo({...todo, checked: !todo.checked});
  };

  const handleSave = () => {
    setIsEdit(false);
    onSave(todo);
  };

  return (
    <>
    <div className='form'>
      <input 
        type='checkbox' 
        checked={todo.checked}
        onChange={handleChange}
      />
      <div className='inputs'>
        <div className="title-wrapper">
        <input 
          type='text'
          disabled={!isEdit}
          className='title'
          placeholder='Adicionar título' 
          value={todo.title}
          onChange={event => setTodo({...todo, title: event.target.value})}
          />
        <FontAwesomeIcon className='icon-button' onClick={toggleCalendar} icon={faCalendar}></FontAwesomeIcon>
     {
      isEdit ?
       <FontAwesomeIcon className='icon-button' onClick={handleSave} icon={faCircleCheck}></FontAwesomeIcon>
       : <FontAwesomeIcon className='icon-button' onClick={toggleEdit} icon={faPencil}></FontAwesomeIcon>

     }

    {/* </button> */}
      </div>
      {
        isCalendarOpen &&
        <DatePicker 
        calendarClassName='calendar'
        inline
        selected={todo.date} 
        dateFormat={"dd/MM/yyyy"} 
        onChange={handleCalendarChange}
        />
      }
        <input 
          type='text'
          className='description'
          placeholder='Adicionar descrição'
          value={todo.description}
          onChange={event => setTodo({...todo, description: event.target.value})}
        />
        <div className='date-label'>{todo?.date?.getDate()}</div>
      </div>
      <div>


      </div>

    </div>
    </>
  );
}

export default InputComponent;
