import { Task } from '../model/task';

import '../styles/InputComponent.css';
import "react-datepicker/dist/react-datepicker.css";
import DatePicker from 'react-datepicker';

function InputComponent({placeholder, label, name, value, onChange, errorMessage}: {placeholder: string, label:string, name: string, value: string, onChange: (value: string) => void, errorMessage:string}) {

  return (
    <>
    <label htmlFor={name}>
      {label}
    </label>
    <input
      type='text'
      disabled={!isEdit}
      className='title'
      placeholder={placeholder}
      value={todo.title}
      onChange={event => setTodo({...todo, title: event.target.value})}
      />
    </>
  );
}

export default InputComponent;
